import React, { useState } from 'react';

const Form = ({ onSubmit, onSummaryReceived }) => {
  const [data, setData] = useState({
    earnings: '',
    expenses: '',
    mileage: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Chama a função onSubmit passada como prop para enviar os dados para o backend
    await onSubmit(data);

    // Chama a função onSummaryReceived passada como prop para buscar e exibir o resumo
    onSummaryReceived();
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Earnings:
        <input
          type="number"
          name="earnings"
          value={data.earnings}
          onChange={handleChange}
        />
      </label>
      <label>
        Expenses:
        <input
          type="number"
          name="expenses"
          value={data.expenses}
          onChange={handleChange}
        />
      </label>
      <label>
        Mileage:
        <input
          type="number"
          name="mileage"
          value={data.mileage}
          onChange={handleChange}
        />
      </label>
      <button type="submit">Submit</button>
    </form>
  );
};

export default Form;
