import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000/api'; // Altere para a URL correta do seu backend

export const fetchDataFromBackend = async (route) => {
  try {
    const response = await axios.get(`${API_BASE_URL}${route}`);
    return response.data;
  } catch (error) {
    console.error('Error fetching data from backend:', error);
    return [];
  }
};
