import React, { useState } from 'react';
import axios from 'axios';

const DailyForm = () => {
  const [earningsValue, setEarningsValue] = useState('');
  const [expenseValue, setExpenseValue] = useState('');
  const [mileageValue, setMileageValue] = useState('');
  const [selectedDate, setSelectedDate] = useState(new Date());

  const handleGainChange = (event) => {
    setEarningsValue(event.target.value);
  };

  const handleExpenseChange = (event) => {
    setExpenseValue(event.target.value);
  };

  const handleMileageChange = (event) => {
    setMileageValue(event.target.value);
  };

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const submitDailyData = async (data) => {
    try {
      await axios.post('/api/gains', data);
      await axios.post('/api/expenses', data);
      await axios.post('/api/mileage', data);
      alert('Dados enviados com sucesso!');
    } catch (error) {
      alert('Ocorreu um erro ao enviar os dados.');
      console.error(error);
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    const data = {
      gain: earningsValue,
      expense: expenseValue,
      mileage: mileageValue,
      date: selectedDate.toISOString(),
    };
    submitDailyData(data);
  };

  return (
    <div>
      <h2>Informe os dados di√°rios:</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Ganho:
          <input type="number" value={earningsValue} onChange={handleGainChange} />
        </label>
        <label>
          Gasto:
          <input type="number" value={expenseValue} onChange={handleExpenseChange} />
        </label>
        <label>
          Quilometragem:
          <input type="number" value={mileageValue} onChange={handleMileageChange} />
        </label>
        <label>
          Data:
          <input type="date" value={selectedDate} onChange={(e) => handleDateChange(new Date(e.target.value))} />
        </label>
        <button type="submit">Enviar</button>
      </form>
    </div>
  );
};

export default DailyForm;
