import React, { useState, useEffect } from 'react';
import axios from 'axios';
import StatCard from './StatCard';

const MonthlyPerformance = () => {
  const [monthlyData, setMonthlyData] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('/api/summary/monthly');
        setMonthlyData(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h2>Monthly Performance</h2>
      <div className="stat-cards">
        <StatCard title="Net Profit" value={monthlyData.netProfit} />
        <StatCard title="Gross Earnings" value={monthlyData.grossEarnings} />
        <StatCard title="Expenses" value={monthlyData.expenses} />
        <StatCard title="Mileage" value={monthlyData.mileage} />
      </div>
    </div>
  );
};

export default MonthlyPerformance;
