import React, { useState, useEffect } from 'react';
import axios from 'axios';
import StatCard from './StatCard';
import API_BASE_URL from '../apiConfig';

const MonthlyPerformance = () => {
  const [monthlyData, setMonthlyData] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`${API_BASE_URL}/api/summary/monthly`);
        setMonthlyData(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h2>Monthly Performance</h2>
      <div className="stat-cards">
        <StatCard title="Total Earnings" value={monthlyData.totalEarnings} />
        <StatCard title="Total Expenses" value={monthlyData.totalExpenses} />
        <StatCard title="Total Mileage" value={monthlyData.totalMileage} />
        <StatCard title="Daily Profit" value={monthlyData.dailyProfit} />
        <StatCard title="Profit per Mile" value={monthlyData.profitPerMile} />
        <StatCard title="Average Earning" value={monthlyData.averageEarning} />
        <StatCard title="Average Expenses" value={monthlyData.averageExpenses} />
        <StatCard title="Average Daily Profit" value={monthlyData.averageDailyProfit} />
        <StatCard title="Average Profit per Mile" value={monthlyData.averageProfitPerMile} />
      </div>
    </div>
  );
};

export default MonthlyPerformance;
