import React, { useState, useEffect } from 'react';
import axios from 'axios';
import StatCard from './StatCard';
import DailyForm from './DailyForm';

const WeeklyPerformance = () => {
  const [weeklyData, setWeeklyData] = useState([]);
  const [dailyData, setDailyData] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('/api/weekly-performance');
      setWeeklyData(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const handleAddDailyData = async (dailyData) => {
    try {
      const response = await axios.post('/api/daily-performance', dailyData);
      setDailyData([...dailyData, response.data]);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div>
      <h2>Weekly Performance</h2>
      <div className="weekly-stats">
        {/* Renderiza as estatÃ­sticas semanais */}
        {weeklyData.map((stat) => (
          <StatCard key={stat.week} title={`Week ${stat.week}`} value={stat.value} />
        ))}
      </div>
      <DailyForm onAddDailyData={handleAddDailyData} />
    </div>
  );
};

export default WeeklyPerformance;
