import React, { useState, useEffect } from 'react';
import DailyChart from './DailyChart';
import WeeklyChart from './WeeklyChart';
import MonthlyChart from './MonthlyChart';
import { getDailyData, getWeeklyData, getMonthlyData, saveDailyData } from '../services/api';

const HomePage = () => {
  const [dailyData, setDailyData] = useState([]);
  const [weeklyData, setWeeklyData] = useState([]);
  const [monthlyData, setMonthlyData] = useState([]);
  const [earnings, setEarnings] = useState('');
  const [expenses, setExpenses] = useState('');
  const [mileage, setMileage] = useState('');

  useEffect(() => {
    fetchDailyData();
    fetchWeeklyData();
    fetchMonthlyData();
  }, []);

  const fetchDailyData = async () => {
    try {
      const data = await getDailyData();
      setDailyData(data);
    } catch (error) {
      console.log('Error fetching daily data:', error);
    }
  };

  const fetchWeeklyData = async () => {
    try {
      const data = await getWeeklyData();
      setWeeklyData(data);
    } catch (error) {
      console.log('Error fetching weekly data:', error);
    }
  };

  const fetchMonthlyData = async () => {
    try {
      const data = await getMonthlyData();
      setMonthlyData(data);
    } catch (error) {
      console.log('Error fetching monthly data:', error);
    }
  };

  const handleSaveData = async () => {
    if (earnings && expenses && mileage) {
      try {
        await saveDailyData(earnings, expenses, mileage);
        // Atualizar os gráficos após salvar os dados
        fetchDailyData();
        fetchWeeklyData();
        fetchMonthlyData();
        // Limpar os campos de entrada
        setEarnings('');
        setExpenses('');
        setMileage('');
      } catch (error) {
        console.log('Error saving daily data:', error);
      }
    }
  };

  return (
    <div>
      <div>
        <h2>Gráfico Diário</h2>
        <DailyChart data={dailyData} />
      </div>
      <div>
        <h2>Gráfico Semanal</h2>
        <WeeklyChart data={weeklyData} />
      </div>
      <div>
        <h2>Gráfico Mensal</h2>
        <MonthlyChart data={monthlyData} />
      </div>
      <div>
        <h2>Inserir Dados Diários</h2>
        <div>
          <label>Earnings:</label>
          <input type="number" value={earnings} onChange={(e) => setEarnings(e.target.value)} />
        </div>
        <div>
          <label>Expenses:</label>
          <input type="number" value={expenses} onChange={(e) => setExpenses(e.target.value)} />
        </div>
        <div>
          <label>Mileage:</label>
          <input type="number" value={mileage} onChange={(e) => setMileage(e.target.value)} />
        </div>
        <button onClick={handleSaveData}>Enviar</button>
      </div>
    </div>
  );
};

export default HomePage;
