import React, { useState, useEffect } from 'react';
import axios from 'axios';
import StatCard from './StatCard';

const WeeklyPerformance = () => {
  const [weeklyData, setWeeklyData] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('/api/summary/weekly');
        setWeeklyData(response.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      <h2>Weekly Performance</h2>
      <div className="stat-cards">
        <StatCard title="Total Earnings" value={weeklyData.totalEarnings} />
        <StatCard title="Total Expenses" value={weeklyData.totalExpenses} />
        <StatCard title="Total Mileage" value={weeklyData.totalMileage} />
        <StatCard title="Daily Profit" value={weeklyData.dailyProfit} />
        <StatCard title="Profit per Mile" value={weeklyData.profitPerMile} />
        <StatCard title="Average Earning" value={weeklyData.averageEarning} />
        <StatCard title="Average Expenses" value={weeklyData.averageExpenses} />
        <StatCard title="Average Daily Profit" value={weeklyData.averageDailyProfit} />
        <StatCard title="Average Profit per Mile" value={weeklyData.averageProfitPerMile} />
      </div>
    </div>
  );
};


export default WeeklyPerformance;
